 navArgument("totalAmountCents") { type = NavType.IntType },
 navArgument("subtotal") { type = NavType.StringType },
 navArgument("estimatedFee") { type = NavType.StringType },
 navArgument("itemsCount") { type = NavType.IntType }
 )
 ) { backStackEntry ->

