@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun AddressesScreen(
 viewModel: AddressMapViewModel,
 onBack: () -> Unit,
 onAddClicked: () -> Unit,
 onAddressClick: (Address) -> Unit
) {
 val addresses by viewModel.addresses.collectAsState()

 Column(
 modifier = Modifier
 .fillMaxSize()
 .background(Color.White)
 ) {

 CustomTopAppBar(
 title = "Addresses",
 onBackClick = onBack,
 actions = {
 Text(
 text = "Add",
 color = Color.Blue,
 fontWeight = FontWeight.Medium,
 fontSize = 18.sp,
 modifier = Modifier
 .clickable {
 onAddClicked()
 viewModel.resetForAddMode()
 }
 .padding(horizontal = 8.dp)
 )
 }
 )


 LazyColumn(
 modifier = Modifier.fillMaxSize(),
 verticalArrangement = Arrangement.spacedBy(8.dp),
 ) {
 items(
 count = addresses.size,
 key = { index -> index }
 ) { index ->
 AddressItem(
 address = addresses[index],
 onClick = {
 onAddressClick(addresses[index])
 viewModel.setupForEditMode(addresses[index])
 }
 )
 HorizontalDivider(
 thickness = 1.dp,
 color = Color(0xFFF2F2F2),
 modifier = Modifier.padding(horizontal = 8.dp)
 )
 }
 }
 }
}

@Composable
fun AddressItem(address: Address, onClick: () -> Unit) {
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(horizontal = 12.dp, vertical = 6.dp),
 shape = RoundedCornerShape(16.dp),
 border = BorderStroke(2.dp, Color.Blue),
 elevation = CardDefaults.cardElevation(defaultElevation = 2.dp),
 onClick = onClick

 ) {
 Row(
 modifier = Modifier
 .background(Color.White)
 .padding(16.dp),
 verticalAlignment = Alignment.CenterVertically
 ) {
 Icon(
 imageVector = when (address.type) {
 AddressType.HOME -> Icons.Outlined.House
 AddressType.APARTMENT -> Icons.Filled.Home
 AddressType.OFFICE -> Icons.Filled.Business
 },
 contentDescription = address.type.name,
 tint = Color.Blue,
 modifier = Modifier.size(32.dp)
 )
 Spacer(modifier = Modifier.width(16.dp))
 Column(modifier = Modifier.weight(1f)) {
 Text(
 text = address.type.name,
 style = MaterialTheme.typography.titleMedium,
 color = Color.Blue
 )
 Text(
 text = address.area,
 style = MaterialTheme.typography.bodyMedium,
 color = Color.Black
 )
 Text(
 text = "${address.street}, ${address.building}",
 style = MaterialTheme.typography.bodySmall,
 color = Color.Gray
 )
 }
 }
 }
}

