 Log.d("CheckoutScreen", "CheckoutScreen: $location")
 Scaffold(
 topBar = {
 CustomTopAppBar(
 title = "Checkout",
 onBackClick = onBack,
 )
 },
 bottomBar = {
 ConfirmOrderBottomBar(
