 CircularProgressIndicator(
 color = Color.White,
 strokeWidth = 2.dp,
 modifier = Modifier.size(24.dp)
 )
 } else {
 Text(
 text = "Place Order",
 color = Color.White,
 fontWeight = FontWeight.Bold,
 fontSize = 16.sp
 )
 }
 }
 }
