 singleLine = false,
 modifier = Modifier.fillMaxWidth(),
 textStyle = TextStyle(color = Color.Black)

 )
 Spacer(Modifier.height(8.dp))
 OutlinedTextField(
 value = addressLabel, onValueChange = { addressLabel = it },
 label = { Text("Address label (optional)") },
 placeholder = { Text("Give this address a label so you can easily choose between them (e.g. Parent's home)") },
 singleLine = true,
 modifier = Modifier.fillMaxWidth(),
 textStyle = TextStyle(color = Color.Black)

 )
 Spacer(Modifier.height(24.dp))

 // Save address button
 Button(
 onClick = {
 currentLocation?.let { location ->
 val newAddress = Address(
 type = addressType,
 area = formattedAddress,
 building = buildingName,
 apartment = aptNumber,
 floor = floor,
 street = street,
 phoneNumber = phoneNumber,
 additionalDirections = additionalDirections,
 addressLabel = addressLabel,
 latitude = location.latitude,
 longitude = location.longitude
 )
 onSave(newAddress)
 }
 },
 enabled = isSaveEnabled,
 modifier = Modifier
// .padding(horizontal = 8.dp)
 .fillMaxWidth()
 .height(56.dp),

 colors = ButtonDefaults.buttonColors(
 containerColor = Color.Blue,
 contentColor = Color.White,
 disabledContainerColor = Color.Gray,
 disabledContentColor = Color.White
 ),
 shape = RoundedCornerShape(8.dp)
 ) {

 Text(
 if(initialAddress !=null) {
 "Update address"
 } else {
 "Save address"
 })
 }
 Spacer(Modifier.height(32.dp))
 }
 }
 }
}


