 composable<ScreensRoute.Search> {
 SearchScreen(
 onBack = { navHostController.popBackStack() },
 onProductClick = { productId ->
 navHostController.navigate(ScreensRoute.ProductDetails(productId))
 }
 )
 }

 composable<ScreensRoute.MapSearch> {
 MapSearch(
 onBack = { navHostController.popBackStack() },
