 .fillMaxWidth()
 .padding(horizontal = 16.dp, vertical = 8.dp)
 .background(Color.White, RoundedCornerShape(16.dp))
 .border(1.dp, Color(0xFFF2F2F2), RoundedCornerShape(16.dp))
