 Log.i("TAG", "MainScreen:${destination.route} ")
 when {
 destination.route in listOf(
 "com.example.m_commerce.presentation.utils.routes.ScreensRoute.Start",
 "com.example.m_commerce.presentation.utils.routes.ScreensRoute.Login",
