 LaunchedEffect(address) {
 if (formattedAddress.isEmpty() || isAddMode) {
 formattedAddress = address.split(",")
 .map { it.trim() }
 .filter { it.isNotEmpty() }
 .let { parts ->
 listOfNotNull(
 parts.getOrNull(1),
 parts.getOrNull(2)
 ).joinToString(", ")
 }
 }
